import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import TaskCategoryContainer from './Containers/TaskCategoryContainer'
import TaskContainer from './Containers/TaskContainer'

class App extends React.Component {

  state = {
    Categories: CATEGORIES
  }

  componentDidMount() {
    fetch("http://localhost:4000/tasks")
      .then(resp => resp.json())
      .then(taskArray => {
        this.setState(prevState => ({ Categories: prevState.Categories, taskArray}))
      })
  }

  render() {
    console.log(this.state)
    return (
      <div className="App">
        <h2>My tasks</h2>
        <TaskCategoryContainer categories={this.state.Categories} />
        <TaskContainer categories={this.state.Categories} tasks={this.state.tasks}/>
      </div>
    );
  }
}


export default App;
