  }
    else {
     return props.tasks.map(task =>
<div className="task">
        <div className="label">{props.tasks.category}</div>
        <div className="text">{props.tasks.text}</div>
      </div>


    )
    }

  }


  return (
    <>
      {categoryRow}

    </>
  )

}

export default TaskComponent
