import React from 'react'

function TaskComponent(props) {

  const categoryRow = () => {
    console.log(props.tasks)
    return props.tasks.map(task =>

      <div className="task">
        <div className="label">{task.category}</div>
        <div className="text">{task.text}</div>
      </div>

    )
  }


  return (
    <>
      {CategoryRow()}
    </>
  )

}

export default TaskComponent
